import Vue from 'vue';
import Router from 'vue-router';
Vue.use(Router);

const router = new Router({
  scrollBehavior(to, from, savedPosition) {
    //路由中设置滚动
    if (to.hash) {
      return {
        selector: to.hash
      };
    } else {
      return {
        x: 0,
        y: 0
      };
    }
  },
  routes: [
    {
      path: '/',
      redirect: '/homePage',
    },
    {
      path: "/homePage",//首页
      name: "homePage",
      component: () => import("@/views/homePage"),
      meta: {
        title: "首页"
      },
    },
    {
      path: "/otherPage",
      name: "otherPage",
      component: () => import("@/views/otherPage/otherPage.vue"),
      meta: {
        title: "其他"
      },
    },
  ]
});

router.beforeEach((to, from, next) => {
  const title = to.meta && to.meta.title;
  if (title) {
    document.title = title;
  }
  next();
});

export default router;
