import Vue from 'vue';
import Router from 'vue-router';
Vue.use(Router);

const router = new Router({

  routes: [
    {
      path: '/',
      redirect: '/index',
    },
    {
      path: "/index",//首页
      name: "index",
      component: () => import("@/views/index"),
      meta: {
        title: "首页"
      },
    },
  ]
});

router.beforeEach((to, from, next) => {
  const title = to.meta && to.meta.title;
  if (title) {
    document.title = title;
  }
  next();
});
export default router;
