import Vue from 'vue';
import Router from 'vue-router';
Vue.use(Router);

const router = new Router({
  routes: [
    {
      path: '/',
      redirect: '/indexPage',
    },
    {
      path: "/indexPage",//首页
      name: "indexPage",
      component: () => import("../views/indexPage/indexPage.vue"),
      meta: {
        title: "首页"
      },
    },
  ]
});

router.beforeEach((to, from, next) => {
  const title = to.meta && to.meta.title;
  const headerObj = to.meta && to.meta.headerObj
  if (title) {
    document.title = title;
  }
  // if (headerObj) {
  //   store.commit("SET_HEADER", headerObj)
  // }
  next();
});
router.afterEach((to, from) => {
  const headerObj = to.meta && to.meta.headerObj
  // if(!headerObj) {
  //   store.commit("CLEAR_HEADER")
  // }
})


export default router;
